INSERT INTO `unit_enemy_data` VALUES (205805,"子フォギー後衛",205802,0,0,270,200,1,1.8,0,0,"フォギー召喚用モンスター（強）");
INSERT INTO `unit_enemy_data` VALUES (206000,"トドドン",206000,0,1,270,100,1,4,0,0,"【物理】近接攻撃\n身体も態度も巨漢な北海のドン。\n纏う厚い脂肪が敵からの物理攻撃\nを押止める。");
INSERT INTO `unit_enemy_data` VALUES (206100,"ワイズワンド",206100,0,7,270,1080,2,4.5,0,0,"【魔法】遠距離攻撃\n浴びれば凍える冷気の魔法で、\n戦意を奪うオウムガイ型の魔物。");
INSERT INTO `unit_enemy_data` VALUES (206200,"ギャングオッター",206200,0,1,270,650,1,3.5,0,0,"【物理】中距離攻撃\n貝殻をぶつけて不意をつく、\n意地悪な北海のチンピラっこ。");
INSERT INTO `unit_enemy_data` VALUES (206300,"ルナポッド",206300,0,0,270,1110,0,10,0,0,"【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。");
INSERT INTO `unit_enemy_data` VALUES (206400,"リトルアイ",206400,0,0,270,100,1,2.5,0,0,"【物理】近接攻撃\n一ッ目族の王に仕える、小さな召使い。\n王様がくれた金ピカ装備が、大のお気に入り。");
INSERT INTO `unit_enemy_data` VALUES (300100,"ミノタウロス",300100,0,3,270,1000,1,1.9,0,0,"【物理】迷宮に巣食う、牡牛座の魔星獣。\n巨大な戦斧を振り回して、冒険者の命を奪う。\n\n・敵全体に物理大ダメージを与え、火傷状態\n　にする。\n・敵1キャラに物理大ダメージを与え、ノック\n　バックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法中ダメージを与え、\n　麻痺させる。");
INSERT INTO `unit_enemy_data` VALUES (300200,"レサトパルト",300200,0,6,270,1000,1,1.5,0,0,"猛毒の尾棘で災厄を振りまく、蠍座の魔星獣。\n砂漠を護る神蟲が、信仰を失い邪神と化した。\n\n・敵全体に物理大ダメージを与える。\n　ボスがユニオンバーストを使用する毎に、\n　敵に与えるダメージがアップ。\n　この効果は、最大３回まで累積する。\n・前方範囲内の敵に物理中ダメージを与え、\n　毒状態にする。\n・自身にかかっているステータスダウン効果を\n　解除し、さらに自身の物理防御力をアップ。");
INSERT INTO `unit_enemy_data` VALUES (300300,"メサルティム",300300,0,7,270,1000,2,2.5,0,0,"【魔法】夢魔の館に住む、牡羊座の魔星獣。\n冒険者を夢世界に束縛し、魂を喰らう。\n\n・敵全体に魔法大ダメージを与え、束縛状態\n　の敵にはさらに魔法小ダメージを与える。\n・前方範囲内の敵に魔法中ダメージを与える。\n・敵1キャラを束縛状態にする。");
INSERT INTO `unit_enemy_data` VALUES (300500,"リザードマン",300500,0,4,270,1000,1,1.8,0,0,"【物理】二刀を自在に操り、\n煙幕弾で相手の目を\nくらませる\nオオトカゲの戦士。\n\n・ランダムな1キャラに物理\n　中ダメージを与える。\n\n・ランダムな敵を中心にした小範囲\n　に、物理小ダメージを与え、\n　暗闇状態にする。");
INSERT INTO `unit_enemy_data` VALUES (300600,"クリーパー",300600,0,2,270,1000,1,1.8,0,0,"【物理】頭から放つ毒攻撃と\nツタによる範囲物理攻撃で\n近づく冒険者の体力を一気に奪う\n悪魔の花。\n\n・前方2キャラに物理中ダメージ\n　を与える。\n\n・前方3キャラの物理防御力を\n　下げ、毒状態にする。");
INSERT INTO `unit_enemy_data` VALUES (300601,"ニードルクリーパー",300601,0,2,270,1000,1,1.8,0,0,"【物理】特異な力で変質したクリーパー。\n無数のトゲを持つ蔓を巻き付け、動きを奪う。\n\n・前方範囲内の敵に物理中ダメージを与え、\n　拘束状態にする。\n・前方範囲内の敵の物理防御力を下げ、毒\n　状態にする。");
INSERT INTO `unit_enemy_data` VALUES (300602,"ニードルクリーパー",300602,0,2,270,1000,1,1.8,0,0,"【物理】特異な力で変質したクリーパー。\n無数のトゲを持つ蔓を巻き付け、動きを奪う。\n\n・前方範囲内の敵に物理中ダメージを与え、\n　拘束状態にする。\n・前方範囲内の敵の物理防御力を下げ、毒\n　状態にする。");
INSERT INTO `unit_enemy_data` VALUES (300700,"オークリーダー",300700,0,13,270,1000,1,1.5,0,0,"【物理】族長として群れを鼓舞し\n大地を砕くほどの連撃で\n敵に大ダメージを与える\nオーク中のオーク。\n\n・ランダムな敵を中心にした\n　3キャラに物理中ダメージを与え\n　物理防御力を上げる。\n\n・味方全体の物理攻撃力を上げる。");
INSERT INTO `unit_enemy_data` VALUES (300701,"オークチーフ",300701,0,13,270,1000,1,1.5,0,0,"特異な力で変質したオークリーダー。\n巨大な盾で、攻防一体の一撃を繰り出す。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方範囲内の敵の防御力を大幅に下げる。");
INSERT INTO `unit_enemy_data` VALUES (300800,"エルダーホーン",300800,0,1,270,1000,1,1.8,0,0,"【物理】ひとたび暴れ出せば\n手が付けられず、その嘶きは\n天まで届くと言われる\n伝説のワイルドホーン。\n\n・敵全体に物理中ダメージを与え、\n　小さく吹き飛ばす。\n\n・前方3キャラに物理中ダメージを\n　与え、物理攻撃力と\n　物理防御力を下げる。");
INSERT INTO `unit_enemy_data` VALUES (300801,"スピリットホーン",300801,0,1,270,1000,1,1.8,0,0,"【物理】特異な力で変質したエルダーホーン。\n角に魔力を込め、打撃より強力な一撃を放つ。\n\n・前方範囲内の敵に魔法大ダメージを与え、\n　ノックバックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法小ダメージを与え、\n　魔法防御と攻撃速度を小ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (300900,"エルダータイラント",300900,0,13,270,1000,1,1.8,0,0,"【物理】強力な頭突きで地を砕き\n全体に大ダメージを与える。\n身体や甲羅が巨大に進化した\n岩山のようなタートルタイラント。");
INSERT INTO `unit_enemy_data` VALUES (301000,"シードレイク",301000,0,14,270,1000,1,1.8,0,0,"【物理】強力な氷のブレスで\n前衛を一瞬で氷漬けにし、\n咬みついて身体を砕く\n獰猛な深海竜。\n\n・前方3キャラに物理中ダメージを\n　与える。\n\n・前方2キャラに物理中ダメージを\n　与え、凍結状態にする。");
INSERT INTO `unit_enemy_data` VALUES (301100,"サベージベア",301100,0,13,270,1000,1,1.7,0,0,"【物理】体格にそぐわぬ俊敏さと\n咬みつき攻撃で体力を奪い、\n巨大なツメで敵を引き裂く\n残虐非道な森の支配者。\n\n・前方4キャラに物理大ダメージを\n　与える。\n\n・前方1キャラに物理小ダメージを\n　与え、物理攻撃力を下げる。\n　さらに、自分の物理攻撃力を\n　上げる。");
INSERT INTO `unit_enemy_data` VALUES (301101,"マッドベア",301101,0,13,270,1000,1,2,0,0,"特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方の敵1キャラに物理小ダメージを与え、\n　物理攻撃力を中ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (301200,"グレーターゴーレム",301200,0,13,270,1000,1,1.6,0,0,"【物理】際限なくゴーレムを召喚し\n岩の針で体力と精神を同時に削る。\n無数のゴーレムの残骸を\n魔力で再構築した巨大なゴーレム。\n\n・前方4キャラに物理中ダメージを\n　与え、ＴＰを下げる。\n\n・ゴーレムを召喚する。");
INSERT INTO `unit_enemy_data` VALUES (301300,"グラットン",301300,0,4,270,1000,1,3,0,0,"暴食の凶獣として悪名轟く、天秤座の魔星獣。\n奪った魂の善悪をはかり、味の違いを愉しむ。\n\n・敵からの攻撃による麻痺、スタン、束縛、\n　行動速度低下効果への耐性を持たない。\n・敵全体に魔法大ダメージを与え、さらに自\n　身の物理攻撃力と魔法攻撃力を特大アップ。\n・後方範囲内の敵に魔法中ダメージ。\n・前方範囲内の敵に魔法中ダメージを与え、Ｔ\n　Ｐを小ダウン。さらに自身のＴＰを中アップ\n　させる。");
INSERT INTO `unit_enemy_data` VALUES (301400,"メデューサ",301400,0,3,270,1000,1,2.5,0,0,"無数の毒蛇から成る頭髪の、乙女座の魔星獣。\n悲恋の苦しみは、全てを拒み石に変える。\n\n・敵全体に魔法大ダメージを与え、前方範囲内\n　の敵はさらに石化状態にする。\n・前方範囲内の敵に物理ダメージを与え、石化\n　状態の敵はさらにＴＰを大ダウン。\n・物理攻撃力が最も高い敵1キャラに魔法大ダ\n　メージを与え、毒状態にする。");
INSERT INTO `unit_enemy_data` VALUES (301500,"ウールヴヘジン",301500,0,12,270,1000,1,1.8,0,0,"【物理】遠吠えで獲物を麻痺させ\nひるんだ所をツメで引き裂く。\n数々の冒険者の行く手を阻んできた\n残虐を極める魔界の狼。\n\n・ランダムな2キャラに物理\n　大ダメージを与える。\n\n・前方3キャラに魔法小ダメージを\n　与え、麻痺状態にする。");
INSERT INTO `unit_enemy_data` VALUES (301600,"ジズ",301600,0,3,270,1000,1,1.8,0,0,"【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。");
INSERT INTO `unit_enemy_data` VALUES (301601,"ジズ",301600,0,3,270,1000,1,1.8,0,0,"【物理】猛り狂う暴風を纏いし大怪鳥。\n魔物図鑑を抜け出し、破壊の限りを尽くす。\n\n・敵全体にキャラの最大ＨＰに応じた\n　大ダメージを与える。\n・前方範囲内の敵に物理中ダメージを与え、\n　さらにスタンさせる。\n・前方範囲内の敵にキャラの最大ＨＰに応じた\n　中ダメージを与え、物理防御力を下げる。\n・ジズ自身のＨＰが50％を切ると、さらに\n　攻撃が激しく凶暴になる。");
INSERT INTO `unit_enemy_data` VALUES (301800,"サジタリウス",301800,0,3,270,1000,1,2,0,0,"天を駆け数多の標的を射止める射手座の魔星獣。\n雷光纏いし矢は、刹那にすべてを穿つ至高の技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与える。\n　暗闇状態の敵はダメージが3倍になる。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵1キャラに強力な暗\n　闇状態を付与。\n・前方の敵3キャラに物理ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (301900,"ウェアオルカ",301900,0,11,270,1000,1,1.7,0,0,"【物理】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。\n\n・前方4キャラに物理中ダメージを\n　与え、物理攻撃力を下げる。\n\n・前方2キャラに魔法小ダメージを\n　与え、ＴＰを下げる。");
INSERT INTO `unit_enemy_data` VALUES (302000,"ワイルドグリフォン",302000,0,11,270,1000,1,1.6,0,0,"過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (302001,"ワイルドグリフォン",302001,0,11,270,1000,1,1.6,0,0,"過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (302100,"ワイバーン",302100,0,11,270,1000,1,1.6,0,0,"大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・前方範囲内の敵に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (302101,"ワイバーン",302101,0,11,270,1000,1,1.6,0,0,"大きな皮翼を使い、空を駆ける翼竜。\n巨大な火球を吐き、侵入者の行く手を阻む。\n\n・前方範囲内の敵に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (302200,"イクト・ガラディオン",302200,0,16,270,1000,2,1.6,0,0,"【魔法】ルナの魔力で目覚めた、古代の機械兵器。\n無慈悲に放たれる魔力が、侵入者を阻む。\n\n・敵全体に魔法特大ダメージを与え、魔法防御力\n　を大ダウン。\n・前方範囲内の敵に物理大ダメージを与える。\n・前方範囲内の敵に魔法大ダメージを与え、\n　麻痺状態にする。");
INSERT INTO `unit_enemy_data` VALUES (302300,"ワイズオウル",302300,0,16,270,1000,2,1.8,0,0,"");
INSERT INTO `unit_enemy_data` VALUES (302400,"アークガーディアン",302400,0,16,270,1000,1,1.6,0,0,"");
INSERT INTO `unit_enemy_data` VALUES (302500,"キマイラ",302500,0,16,270,1000,1,1.4,0,0,"");
INSERT INTO `unit_enemy_data` VALUES (302600,"ツインピッグス",302600,0,16,270,1000,1,1.6,0,0,"【物理】盗賊行為を重ねる、双子座の魔星獣。\n抜群の連携で、アジトの侵入者に襲い掛かる。\n\n・敵全体に、最大ＨＰに応じた物理\n　大ダメージ。\n・後衛の敵に物理中ダメージを与え、\n　攻撃速度を小ダウン。\n・敵全体に、物理小ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (302601,"ツインピッグス",302601,0,16,270,1000,1,1.9,0,0,"【物理】盗賊行為を重ねる、双子座の魔星獣。\n抜群の連携で、アジトの侵入者に襲い掛かる。\n\n・敵全体に、最大ＨＰに応じた物理\n　大ダメージ。\n・後衛の敵に物理中ダメージを与え、\n　攻撃速度を小ダウン。\n・敵全体に、物理小ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (302700,"カルキノス",302700,0,16,270,1000,1,1.8,0,0,"【物理】船墓の入江に巣食う、蟹座の魔星獣。\n巨大なハサミの一撃で、無数の船を沈める。\n\n・敵全体に魔法大ダメージを与え、物理防御力\n　をダウン。\n・前方範囲内の敵すべてに物理中ダメージを与\n　え、さらにスタン状態にする。\n・敵全体に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (302800,"オルレオン",302800,0,16,270,1000,1,1.8,0,0,"サバンナの覇者と名高い、獅子座の魔星獣。\n刃向かう者は、灼熱の咆哮で焼き尽くす。\n\n・敵全体に、最大ＨＰに応じた防御力無視の\n　物理大ダメージを与え、残りＨＰの少ない\n　敵はさらに火傷状態にする。\n・敵1キャラに物理大ダメージを与え、火傷\n　状態の場合はさらに追加ダメージ。\n・前方範囲内の敵に物理中ダメージを与え、\n　火傷状態の場合はさらに追加ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (303100,"トウミングマ",303100,0,13,270,1000,1,3.5,0,0,"【物理】氷穴最奥に引き篭り\n生涯冬眠すると決めた巨大熊。\n長い歳月を夢うつつの中で過ごし\n獲物を狩るその一瞬、覚醒する。\n\n・敵全体に魔法大ダメージを与え、\n　物理攻撃力を下げる。\n\n・前方3キャラに魔法小ダメージを\n　与える。さらに、前方1キャラを\n　大きく吹き飛ばし、氷結状態\n　にして移動速度を下げる。");
INSERT INTO `unit_enemy_data` VALUES (303200,"マダムピック",303200,0,6,270,1000,1,1.6,0,0,"【物理】吹雪と共に現れ、\n縄張りを荒らす者には\n大群を呼び寄せ襲い掛かる\n雪国イチのゴージャスマダム。\n\n・前方4キャラに魔法中ダメージを\n　与え、物理攻撃力を下げる。\n\n・ランダムな2キャラに魔法\n　小ダメージを与え、\n　凍結状態にする。");
INSERT INTO `unit_enemy_data` VALUES (303300,"ガーゴイル",303300,0,13,270,1000,1,2.1,0,0,"【物理】朽ちた遺跡に残り\n主亡き地を護る、謎の鳥像。\n街も命令も、全て砂と消えた今は\n誰彼構わず炎を吐き襲い掛かる。\n\n・前方5キャラに魔法中ダメージを\n　与え、火傷状態にする。さらに、\n　魔法防御力を下げる。\n\n・前方3キャラに魔法小ダメージを\n　与え、暗闇状態にする。");
INSERT INTO `unit_enemy_data` VALUES (303400,"スナノヅチ",303400,0,13,270,1000,1,1.9,0,0,"【物理】生存競争激しい砂漠の\n頂点に君臨する、巨大トカゲ。\n毒を使い、砂を活かす戦法は、\n過酷な環境で得たサバイバル術。\n\n・敵全体に物理中ダメージを与え、\n　行動速度を下げる。\n　さらに、味方全体の物理攻撃力を\n　上げる。\n\n・前方1キャラを中心にした小範囲\n　に物理小ダメージを与え、毒状態\n　にする。さらに、物理防御力を\n　下げる。");
INSERT INTO `unit_enemy_data` VALUES (303500,"ムシュフシュ",303500,0,13,270,1000,1,1.8,0,0,"【物理】吸い続けた他者の\n魔力で、妖術を体得した魔物。\n敵に付けた傷から魔力を奪い、\nさらなる力を得ようとする。\n\n・敵全体に魔法中ダメージを与え、\n　魔法攻撃をする敵すべての\n　ＴＰを下げる。\n\n・前方1キャラを中心にした小範囲\n　に魔法小ダメージを与え、\n　魔法防御力を下げる。");
INSERT INTO `unit_enemy_data` VALUES (303600,"バーンサウルス",303600,0,13,270,1000,1,1.7,0,0,"【物理】強靭な脚と鋭利なツメで\n獲物を一瞬で狩る、獰猛な恐竜。\n滅びた大地でマグマを喰らい、\n生存の道を見つけた孤高の存在。\n\n・敵全体に物理中ダメージを与え、\n　自分の行動速度を上げる。\n\n・前方3キャラに物理中ダメージ\n　を与え、行動速度を下げる。");
INSERT INTO `unit_enemy_data` VALUES (303700,"ヘルハウンド",303700,0,14,270,1000,1,0.7,0,0,"【物理】火山に身を落としてなお\n地獄の炎を纏い蘇った双頭の魔犬。\n愛犬ボニーと共に縄張りを駆け、\n侵入者には灼熱の洗礼を浴びせる。\n\n・敵全体に物理中ダメージを与え、\n　火傷状態にする。さらに、召喚\n　モンスターのＨＰを回復する。\n\n・ボニーを召喚する。");
